import{_ as r,c as d,o,ah as n}from"./chunks/framework.BwTf_eGd.js";const b=JSON.parse('{"title":"VSCode 快捷键手册","description":"","frontmatter":{"title":"VSCode 快捷键手册","layout":"doc"},"headers":[],"relativePath":"develop/vscode_mapping.md","filePath":"develop/vscode_mapping.md"}'),s={name:"develop/vscode_mapping.md"};function e(g,t,a,h,c,i){return o(),d("div",null,t[0]||(t[0]=[n('<h1 id="vscode-快捷键手册" tabindex="-1">VSCode 快捷键手册 <a class="header-anchor" href="#vscode-快捷键手册" aria-label="Permalink to “VSCode 快捷键手册”">​</a></h1><h2 id="_1-切换语言模式" tabindex="-1">1. 切换语言模式 <a class="header-anchor" href="#_1-切换语言模式" aria-label="Permalink to “1. 切换语言模式”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Ctrl + Shift + J</code></td><td>将当前编辑器的语言模式切换为 <strong>JSONC</strong>（注释支持的 JSON 格式）</td><td>适用于 <strong>编辑器聚焦</strong> 的情况</td></tr></tbody></table><hr><h2 id="_2-导航到不同编辑器位置" tabindex="-1">2. 导航到不同编辑器位置 <a class="header-anchor" href="#_2-导航到不同编辑器位置" aria-label="Permalink to “2. 导航到不同编辑器位置”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Ctrl + H</code></td><td>导航到 <strong>左侧编辑器</strong>，将焦点移至左边的编辑区域。</td><td><strong>普通模式</strong> (Vim 模式)</td></tr><tr><td><code>Ctrl + L</code></td><td>导航到 <strong>右侧编辑器</strong>，将焦点移至右边的编辑区域。</td><td><strong>普通模式</strong> (Vim 模式)</td></tr><tr><td><code>Ctrl + K</code></td><td>导航到 <strong>上方编辑器</strong>，将焦点移至上方的编辑区域。</td><td><strong>普通模式</strong> (Vim 模式)</td></tr><tr><td><code>Ctrl + J</code></td><td>导航到 <strong>下方编辑器</strong>，将焦点移至下方的编辑区域。</td><td><strong>普通模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="_3-显示和切换编辑器" tabindex="-1">3. 显示和切换编辑器 <a class="header-anchor" href="#_3-显示和切换编辑器" aria-label="Permalink to “3. 显示和切换编辑器”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Space ,</code></td><td>显示所有打开的编辑器，适用于 Vim <strong>正常模式</strong>，当编辑器或输入框未聚焦时可用。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Tab</code></td><td>切换到当前编辑器组中的 <strong>下一个编辑器</strong>。</td><td><strong>正常模式</strong>、<strong>可视模式</strong> (Vim 模式)</td></tr><tr><td><code>Shift + Tab</code></td><td>切换到当前编辑器组中的 <strong>上一个编辑器</strong>。</td><td><strong>正常模式</strong>、<strong>可视模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="_4-编辑器侧边栏操作" tabindex="-1">4. 编辑器侧边栏操作 <a class="header-anchor" href="#_4-编辑器侧边栏操作" aria-label="Permalink to “4. 编辑器侧边栏操作”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Space E</code></td><td>切换侧边栏的 <strong>可见性</strong> 并聚焦文件资源管理器。适用于 Vim <strong>正常模式</strong>，编辑器聚焦时有效。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space E</code> (侧边栏聚焦时)</td><td>切换侧边栏的 <strong>可见性</strong> 并聚焦活动编辑器组。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="_5-行移动操作" tabindex="-1">5. 行移动操作 <a class="header-anchor" href="#_5-行移动操作" aria-label="Permalink to “5. 行移动操作”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Shift + J</code></td><td>将选中的行 <strong>下移</strong>，在 <strong>Vim 可视行模式</strong> 下有效。</td><td><strong>可视行模式</strong> (Vim 模式)</td></tr><tr><td><code>Shift + K</code></td><td>将选中的行 <strong>上移</strong>，在 <strong>Vim 可视行模式</strong> 下有效。</td><td><strong>可视行模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="_6-光标悬停信息与代码操作" tabindex="-1">6. 光标悬停信息与代码操作 <a class="header-anchor" href="#_6-光标悬停信息与代码操作" aria-label="Permalink to “6. 光标悬停信息与代码操作”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Shift + K</code></td><td>显示光标下符号的 <strong>悬停信息</strong>（例如函数签名、文档注释等）。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space C A</code></td><td>触发 <strong>代码操作</strong>（如修复或重构代码）。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space C R</code></td><td><strong>重命名</strong>光标下的符号。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space C S</code></td><td>跳转到 <strong>代码中的符号</strong>（如函数、类等）。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="_7-编辑器和文件操作" tabindex="-1">7. 编辑器和文件操作 <a class="header-anchor" href="#_7-编辑器和文件操作" aria-label="Permalink to “7. 编辑器和文件操作”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Shift + K</code></td><td>显示光标下符号的 <strong>悬停信息</strong>（例如函数签名、文档注释等）。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space C A</code></td><td>触发 <strong>代码操作</strong>（如修复或重构代码）。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space C R</code></td><td><strong>重命名</strong>光标下的符号。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space C S</code></td><td>跳转到 <strong>代码中的符号</strong>（如函数、类等）。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space B D</code></td><td><strong>关闭当前活动的编辑器</strong>。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space Space</code></td><td>打开 <strong>快速打开对话框</strong>，用于快速打开文件、符号或命令。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space G D</code></td><td>跳转到光标下符号的 <strong>定义</strong>。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space G R</code></td><td>跳转到光标下符号的 <strong>引用</strong>。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space G I</code></td><td>跳转到光标下符号的 <strong>实现</strong>。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Space S G</code></td><td><strong>在文件中查找文本</strong>。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="_8-源代码管理操作" tabindex="-1">8. 源代码管理操作 <a class="header-anchor" href="#_8-源代码管理操作" aria-label="Permalink to “8. 源代码管理操作”">​</a></h2><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Space G G</code></td><td>聚焦 <strong>源代码管理面板</strong>，方便查看版本控制相关的操作。</td><td><strong>正常模式</strong> (Vim 模式)</td></tr><tr><td><code>Ctrl + N</code></td><td>将 <strong>下一个匹配项</strong> 添加到选择中。适用于文本查找中。</td><td><strong>正常模式</strong>、<strong>可视模式</strong> (Vim 模式)</td></tr></tbody></table><hr><h2 id="文件资源管理器操作" tabindex="-1">文件资源管理器操作 <a class="header-anchor" href="#文件资源管理器操作" aria-label="Permalink to “文件资源管理器操作”">​</a></h2><h3 id="_1-文件操作" tabindex="-1">1. 文件操作 <a class="header-anchor" href="#_1-文件操作" aria-label="Permalink to “1. 文件操作”">​</a></h3><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>R</code></td><td><strong>重命名选中的文件</strong>。适用于 <strong>文件资源管理器聚焦时</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>C</code></td><td><strong>复制选中的文件</strong>。适用于 <strong>文件资源管理器聚焦时</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>P</code></td><td><strong>粘贴复制/剪切的文件</strong>。适用于 <strong>文件资源管理器聚焦时</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>X</code></td><td><strong>剪切选中的文件</strong>。适用于 <strong>文件资源管理器聚焦时</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>D</code></td><td><strong>删除选中的文件</strong>。适用于 <strong>文件资源管理器聚焦时</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr></tbody></table><hr><h3 id="_2-文件夹和新文件操作" tabindex="-1">2. 文件夹和新文件操作 <a class="header-anchor" href="#_2-文件夹和新文件操作" aria-label="Permalink to “2. 文件夹和新文件操作”">​</a></h3><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>A</code></td><td><strong>在资源管理器中创建新文件</strong>。适用于 <strong>文件资源管理器聚焦时</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>Shift + A</code></td><td><strong>在资源管理器中创建新文件夹</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>S</code></td><td><strong>将选中的文件/文件夹在侧边打开</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>Shift + S</code></td><td><strong>拆分编辑器，打开选中的项，并关闭其他编辑器</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr></tbody></table><hr><h3 id="_3-打开文件和折叠文件夹" tabindex="-1">3. 打开文件和折叠文件夹 <a class="header-anchor" href="#_3-打开文件和折叠文件夹" aria-label="Permalink to “3. 打开文件和折叠文件夹”">​</a></h3><table tabindex="0"><thead><tr><th><strong>键位</strong></th><th><strong>作用</strong></th><th><strong>生效模式</strong></th></tr></thead><tbody><tr><td><code>Enter</code></td><td><strong>打开选中的文件并聚焦</strong>。适用于文件资源管理器。</td><td><strong>文件资源管理器聚焦时</strong></td></tr><tr><td><code>Enter</code> (文件夹)</td><td><strong>展开或折叠文件夹</strong>。</td><td><strong>文件资源管理器聚焦时</strong></td></tr></tbody></table>',34)]))}const m=r(s,[["render",e]]);export{b as __pageData,m as default};
